import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs/operators';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  showHeaderFooter: boolean = true;  // Asegúrate de que esta propiedad sea pública

  constructor(private router: Router) { }

  ngOnInit(): void {  // Asegúrate de que el método sea público
    this.router.events.pipe(
      filter(event => event instanceof NavigationEnd)
    ).subscribe((event: NavigationEnd) => {
      this.showHeaderFooter = event.urlAfterRedirects !== '/inicio-sesion';
    });
  }
}

<app-header *ngIf="showHeaderFooter"></app-header>
<main>
  <router-outlet></router-outlet>
</main>
<app-footer *ngIf="showHeaderFooter"></app-footer>
