import { Component, OnInit, OnDestroy } from '@angular/core';

@Component({
  selector: 'app-pagina-principal',
  templateUrl: './pagina-principal.component.html',
  styleUrls: ['./pagina-principal.component.css']
})
export class PaginaPrincipalComponent implements OnInit, OnDestroy {
  carouselImages = [
    { src: 'assets/concert1.png', alt: 'Concert Image 1' },
    { src: 'https://via.placeholder.com/1024x400/ff0000', alt: 'Concert Image 2' },
    { src: 'https://via.placeholder.com/1024x400/00ff00', alt: 'Concert Image 3' }
  ];

  concerts = [
    { imgSrc: 'assets/coldplay.png', title: 'Coldplay' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 2' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 3' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 4' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 5' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 6' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 7' },
    { imgSrc: 'assets/coldplay.png', title: 'Concert 8' }
  ];

  currentSlide = 0;
  autoSlideInterval: any;
  currentConcertIndex = 0;
  visibleConcerts: any[] = [];

  ngOnInit(): void {
    this.autoSlide();
    this.showConcerts();
  }

  ngOnDestroy(): void {
    clearInterval(this.autoSlideInterval);
  }

  showSlide(index: number): void {
    this.currentSlide = index;
  }

  autoSlide(): void {
    this.autoSlideInterval = setInterval(() => {
      this.currentSlide = (this.currentSlide + 1) % this.carouselImages.length;
    }, 3000);  // Cambia de imagen cada 3 segundos
  }

  showConcerts(): void {
    this.visibleConcerts = this.concerts.slice(this.currentConcertIndex, this.currentConcertIndex + 4);
  }

  scrollLeft(): void {
    if (this.currentConcertIndex > 0) {
      this.currentConcertIndex -= 4;
      this.showConcerts();
    }
  }

  scrollRight(): void {
    if (this.currentConcertIndex + 4 < this.concerts.length) {
      this.currentConcertIndex += 4;
      this.showConcerts();
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Concerts Carousel</title>
    <link rel="stylesheet" href="pagina-principal.component.css">
</head>
<body>
    <section class="carousel-section">
        <div class="carousel">
          <div class="carousel-image">
            <img *ngFor="let image of carouselImages; let i = index" [src]="image.src" [alt]="image.alt" [class.active]="i === currentSlide">
            <button class="buy-tickets-btn">Comprar boletos</button>
          </div>
          <div class="carousel-dots">
            <span *ngFor="let image of carouselImages; let i = index" class="dot" [class.active]="i === currentSlide" (click)="showSlide(i)"></span>
          </div>
        </div>
      </section>

      <section class="concerts-section">
        <h2>Nuestros Conciertos</h2>
        <div class="concert-cards-wrapper">
          <button class="arrow left" (click)="scrollLeft()">&#9664;</button>
          <div class="concert-cards">
            <div class="concert-card" *ngFor="let concert of visibleConcerts">
              <img [src]="concert.imgSrc" [alt]="concert.title">
              <button class="buy-btn">Comprar</button>
            </div>
          </div>
          <button class="arrow right" (click)="scrollRight()">&#9654;</button>
        </div>
      </section>



</body>
</html>
